#![feature(test)]

extern crate test;
extern crate bio;

use test::{Bencher, black_box};
use bio::alphabets;
use bio::data_structures::suffix_array::{suffix_array, RawSuffixArray, SampleableSuffixArray};
use bio::data_structures::bwt::{BWT, bwt, less, Less, Occ};
use bio::data_structures::fmindex::{fmindex_raw, fmindex_sampled, FMIndexable};

static TEXT: &'static [u8] = b"$";
static PATTERN: &'static [u8] = b"GCGCGTACACACCGCCCG";

fn build_parts() -> (RawSuffixArray, BWT, Occ, Less) {
    let alphabet = alphabets::dna::iupac_alphabet();
    let sa = suffix_array(TEXT);
    let bw = bwt(TEXT, &sa);
    let occ = Occ::new(&bw, 3, &alphabet);
    let lss = less(&bw, &alphabet);

    (sa, bw, occ, lss)
}

#[bench]
fn bench_fmindex_creation_raw_sa(b: &mut Bencher) {
    b.iter(|| {
        let (sa, bw, occ, lss) = build_parts();
        black_box(fmindex_raw(sa, bw, occ, lss));
    });
}

#[bench]
fn bench_fmindex_raw_sa(b: &mut Bencher) {
    let (sa, bw, occ, lss) = build_parts();
    let fm_raw = fmindex_raw(sa, bw, occ, lss);

    b.iter(|| {
        black_box(fm_raw.offsets(PATTERN.iter()));
    });
}

#[bench]
fn bench_fmindex_creation_sampled_sa_32(b: &mut Bencher) {
    b.iter(|| {
        let (sa, bw, occ, lss) = build_parts();
        let ssa = sa.sample(bw, lss, occ, 32);
        black_box(fmindex_sampled(ssa));
    });
}

#[bench]
fn bench_fmindex_sampled_sa_32(b: &mut Bencher) {
    let (sa, bw, occ, lss) = build_parts();
    let ssa = sa.sample(bw, lss, occ, 32);
    let fm_sampled = fmindex_sampled(ssa);

    b.iter(|| {
        black_box(fm_sampled.offsets(PATTERN.iter()));
    });
}
